const processStepCounter = msg.processStepCounter
const processSteps = msg.payload.processSteps
var pressForce = msg.payload.pressForce
const minPressForce = msg.payload.minPressForce
const maxPressForce = msg.payload.maxPressForce
var temperature = msg.payload.temperature
const minTemperature = msg.payload.minTemperature
const maxTemperature = msg.payload.maxTemperature

if(processStepCounter < ( processSteps / 2)){
    // increase press force
    pressForce = pressForce + randomInteger(0, processStepCounter)
    pressForce = ( pressForce > maxPressForce ? maxPressForce : pressForce)
} else {
    // decrease press force
    pressForce = pressForce - randomInteger(0, processStepCounter)
    pressForce = ( pressForce < minPressForce ? minPressForce : pressForce)

}
//console.log("Press Force: " + pressForce)
// emulate temperature during process
if(processStepCounter < ( processSteps / 1.5)){
    // increase temperature
    temperature = temperature + randomInteger(0, 2)
    temperature = ( temperature > maxTemperature ? maxTemperature : temperature)
} else {
    // decrease temperature
    temperature = temperature - randomInteger(0, 1)
    temperature = ( temperature < minTemperature ? minTemperature : temperature)
}

function randomInteger(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

msg.payload.pressForce = pressForce
msg.payload.temperature = temperature

return msg;

